mod metapb {
    tonic::include_proto!("metapb");
}
mod datapb {
    tonic::include_proto!("datapb");
}
use std::sync::Arc;

use datapb::data_rpc_client::DataRpcClient;
use datapb::data_rpc_server::{DataRpc, DataRpcServer};
use datapb::{
    CommitDataReq, CommitDataRsp, ReadDataReq, ReadDataRsp, RemoveDataReq, RemoveDataRsp,
    TransferReq, TransferRsp,
};
use futures::future::join_all;
use metapb::meta_rpc_client::MetaRpcClient;
use metapb::{CreateReq, ReadReq, RemoveReq, WriteReq};
use myraft::async_trait::async_trait;
use tokio::spawn;
use tokio::sync::RwLock;
use tonic::transport::{Channel, Server};
use tonic::{Request, Response, Status};

#[derive(Clone)]
struct MyDataRpc {
    content: Arc<RwLock<Vec<u8>>>,
}

#[async_trait]
impl DataRpc for MyDataRpc {
    async fn read(&self, request: Request<ReadDataReq>) -> Result<Response<ReadDataRsp>, Status> {
        let req = request.into_inner();
        let content = self.content.read().await;
        let range = req.offset as usize..(req.offset + req.size) as usize;
        // println!("!!! {:?}",range);
        let data = content.as_slice()[range].to_vec();
        assert!(data.len() == req.size as usize);
        Ok(Response::new(ReadDataRsp { data }))
    }

    async fn remove(
        &self,
        _request: Request<RemoveDataReq>,
    ) -> Result<Response<RemoveDataRsp>, Status> {
        todo!()
    }

    async fn commit(
        &self,
        _request: Request<CommitDataReq>,
    ) -> Result<Response<CommitDataRsp>, Status> {
        todo!()
    }

    async fn transfer(
        &self,
        _request: Request<TransferReq>,
    ) -> Result<Response<TransferRsp>, Status> {
        todo!()
    }
}

struct CmdClient {
    rpc_addr: String,
    rpc: MyDataRpc,
    meta_client: MetaRpcClient<Channel>,
}

impl CmdClient {
    async fn new() -> Self {
        let rpc = MyDataRpc {
            content: Arc::new(RwLock::new(vec![])),
        };
        let rpc_clone = rpc.clone();
        let rpc_addr = "http://127.0.0.1:54321".to_string();
        let server_addr = "127.0.0.1:54321".parse().unwrap();
        // let server_addr = ;
        spawn(async move {
            Server::builder()
                .add_service(DataRpcServer::new(rpc_clone))
                .serve(server_addr)
                .await
                .unwrap();
        });
        let master_addr = "http://127.0.0.1:12345";
        let client = MetaRpcClient::connect(master_addr).await.unwrap();
        Self {
            rpc_addr,
            rpc,
            meta_client: client,
        }
    }

    async fn create(&mut self, filename: String) {
        let req = Request::new(CreateReq { filename });
        let rsp = self.meta_client.create(req).await;
        println!("{:?}", rsp);
        println!("\n")
    }

    async fn write(&mut self, filename: String, offset: u64, data: Vec<u8>, del_size: u64) {
        let req = Request::new(WriteReq {
            client_addr: self.rpc_addr.clone(),
            filename: filename,
            offset,
            ins: data.len() as u64,
            del: del_size,
        });
        let mut content = self.rpc.content.write().await;
        *content = data;
        drop(content);
        let rsp = self.meta_client.write(req).await;
        println!("{:?}", rsp);
        println!("\n");
    }

    async fn read(&mut self, filename: String, offset: u64, size: u64) {
        let req = Request::new(ReadReq {
            filename,
            offset,
            size,
        });
        let rsp = self.meta_client.read(req).await;
        println!("{:?}", rsp);
        let handlers = rsp
            .unwrap()
            .into_inner()
            .ops
            .into_iter()
            .map(|op| {
                spawn(async move {
                    let mut client = DataRpcClient::connect(op.addr).await.unwrap();
                    client
                        .read(Request::new(ReadDataReq {
                            cid: op.cid,
                            size: op.size,
                            offset: op.offset,
                        }))
                        .await
                })
            })
            .collect::<Vec<_>>();
        let rsps = join_all(handlers).await;
        let mut buf = vec![];
        for rsp in rsps {
            buf.append(&mut rsp.unwrap().unwrap().into_inner().data);
        }
        let data = String::from_utf8(buf).unwrap();
        println!("data: {}", data);
        println!("\n");
    }

    async fn remove(&mut self, filename: String) {
        let req = Request::new(RemoveReq { filename });
        let rsp = self.meta_client.remove(req).await;
        println!("{:?}", rsp);
        println!("\n");
    }
}

#[tokio::main]
async fn main() {
    let mut client = CmdClient::new().await;
    client.create("abc".to_string()).await;
    client
        .write("abc".to_string(), 0, b"".to_vec(), 0)
        .await;
    client.read("abc".to_string(), 0, 11).await;
    client
        .write(
            "abc".to_string(),
            11,
            b"".to_vec(),
            5000,
        )
        .await;
    client.read("abc".to_string(), 5000, 110).await;
    client.remove("abc".to_string()).await;
}
